import { Component } from '@angular/core';

@Component({
  selector: 'app-for-you',
  templateUrl: './for-you.component.html',
  styleUrls: ['./for-you.component.css']
})
export class ForYouComponent {
  compliments = [
    "You have a beautiful smile!",
    "Your kindness brightens my day.",
    "You are incredibly intelligent.",
    "Your strength inspires me.",
    "You have a heart of gold.",
    "You're an amazing listener.",
    "Your creativity knows no bounds.",
    "You have a great sense of humor!",
    "You are truly one of a kind.",
    "You make the world a better place.",
    // Add more compliments as needed
  ];

  complimentsContainer = document.getElementById("compliments-list");
  generateRandomCompliment() {
    const randomIndex = Math.floor(Math.random() * this.compliments.length);
    const compliment = this.compliments[randomIndex];
    const complimentElement = document.createElement("p");
    complimentElement.textContent = compliment;
    this.complimentsContainer.appendChild(complimentElement);
}

// Generate a new compliment every 5 seconds
setInterval(generateRandomCompliment, 5000);
}
